3ae\",\"elType\":\"section\",\"settings\":[],\"elements\":[{\"id\":\"53b05fa\",\"elType\":\"column\",\"settings\":{\"_column_size\":100},\"elements\":[{\"id\":\"d507b5e\",\"elType\":\"widget\",\"settings\":[],\"elements\":[],\"widgetType\":\"spacer\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"6aacb49\",\"elType\":\"section\",\"settings\":{\"structure\":\"20\",\"border_border\":\"dotted\",\"border_width\":{\"unit\":\"px\",\"top\":\"1\",\"right\":\"0\",\"bottom\":\"1\",\"left\":\"0\",\"isLinked\":false},\"border_color\":\"#cccccc\",\"border_radius\":{\"unit\":\"px\",\"top\":\"1\",\"right\":\"0\",\"bottom\":\"1\",\"left\":\"0\",\"isLinked\":false}},\"elements\":[{\"id\":\"4ade06fe\",\"elType\":\"column\",\"settings\":{\"_column_size\":50,\"_inline_size\":null},\"elements\":[{\"id\":\"5ad238\",\"elType\":\"widget\",\"settings\":{\"image\":{\"url\":\"http:\\/\\/localhost:8888\\/gmc\\/wp-content\\/uploads\\/2019\\/02\\/malaysia-flag.jpg\",\"id\":299},\"image_size\":\"custom\",\"image_custom_dimension\":{\"width\":\"144\",\"height\":\"72\"},\"align\":\"right\"},\"elements\":[],\"widgetType\":\"image\"}],\"isInner\":false},{\"id\":\"438a4af\",\"elType\":\"column\",\"settings\":{\"_column_size\":50,\"_inline_size\":null},\"elements\":[{\"id\":\"d4efcea\",\"elType\":\"widget\",\"settings\":{\"title\":\"<br>Study in Malaysia\",\"header_size\":\"h3\",\"title_color\":\"#0c0c0c\"},\"elements\":[],\"widgetType\":\"heading\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"67f30b3\",\"elType\":\"section\",\"settings\":[],\"elements\":[{\"id\":\"be64196\",\"elType\":\"column\",\"settings\":{\"_column_size\":100},\"elements\":[{\"id\":\"4481e93\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<p>The graduate education system in Malaysia is the same as many other countries, such as the UK but there is a higher number of private universities in Malaysia. There is a wide choice of graduate programs including Masters degrees and research PHD studies at Malaysian universities,